/**
*** FLOOR PLAN
**/
typedef double point[2];
bool isInRectangle(point start, double dX, double dY, double length, double height) {
    bool destXisIn, destYisIn;
    if(fabs(length)>fabs(height)) {
        if(length>0) {
            destXisIn = dX < start[0] + 0.95 * length && dX > start[0] - 0.05 * length;    
        } else {
            destXisIn = dX < start[0] + 0.05 * -length && dX > start[0] - 0.95 * -length;            
        }
        destYisIn = dY < start[1] + height / 2 && dY > start[1] - height / 2;
    } else {
        destXisIn = dX < start[0] + length/2 && dX > start[0] - length/2;
        if(height>0) {
            destYisIn = dY < start[1] + 0.95*height && dY > start[1] - 0.005*height;
        } else {
            destYisIn = dY < start[1] + 0.005*-height && dY > start[1] - 0.95*-height;        
        }
    }
    return destXisIn && destYisIn;
}

// Wall Corner Points
point HALL1_1 = {200.0, 400.00};
point HALL1_2 = {1200.00, 400.00};
point HALL1_3 = {1200.00, 600.00};
point HALL1_4 = {1400.00, 600.00};
point HALL2_1 = {200.0, 200.00};
point HALL2_2 = {1200.00, 200.00};
point HALL2_3 = {1200.00, 0.0};
point HALL2_4 = {1400.00, 0.0};

point rechargeStation = {220.0, 300.00};

template.declaration:
clock h, t, F;
clock updateTimer;
clock freeWill;

double lambda = 0.005, mi = 0.005;
double MET = 60.0;
double passout_th = 1.0;

// Free Will Model
int obey;
int disobey;
