    F = 0; 
    t = 0;
    h = 0;

    updateTimer = 0;
    oldFatigue = 0;
    humanPositionX[id-1] = HALL1_1[0]+20;
    humanPositionY[id-1] = HALL2_1[1] + (HALL1_1[1]-HALL2_1[1])/2 + 25;
    oldPosX = humanPositionX[id-1];
    oldPosY = humanPositionY[id-1];
}

