void checkSuccess() {
    bool hXinDestInterval = humanPositionX[id-1] >= destX[id-1]-50.0 && humanPositionX[id-1] <= destX[id-1]+50.0;
    bool hYinDestInterval = humanPositionY[id-1] >= destY[id-1]-50.0 && humanPositionY[id-1] <= destY[id-1]+50.0;
    // when humans reach their destination, they can set themselves as "served"
    if(hXinDestInterval && hYinDestInterval) {
        served[id-1] = true;
    }
}

