bool destIsOnTheLeft, destIsOnTheRight, destIsAhead, destIsBehind;
bool humanCanStart = false;
double dX, dY;
void changeDirection() {
    double inverseValue, turnLeft, turnRight, length=2000.0, height=100.0;
    double toCheck, robThF, robThB;   
    point pos = {robPositionX, robPositionY}; 

    if(patterns[currH-1]==1 || (patterns[currH-1]==0 && !humanCanStart)) {
        dX = humanPositionX[currH-1];
        dY = humanPositionY[currH-1];
    } else if(aStart && recharge) {
        dX = rechargeStation[0];
        dY = rechargeStation[1];
    } else {
        dX = destX[currH-1];
        dY = destY[currH-1];
    }

    destIsOnTheLeft = false; destIsOnTheRight = false; destIsAhead = false; destIsBehind = false;
    if(robTheta == 0) {
        inverseValue = -pi; turnLeft = pi/2; turnRight = -pi/2;
        toCheck = robPositionX;
        robThF = allowanceFactor*HALL2_4[0];
        if(robPositionX > HALL1_3[0] && (robPositionY > HALL1_2[1] || robPositionY < HALL2_2[1])) {
            robThB = HALL1_3[0]+(1-allowanceFactor)*HALL1_3[0];
        } else {
            robThB = HALL2_1[0]+(1-allowanceFactor)*HALL2_1[0];        
        }
        destIsOnTheLeft = isInRectangle(pos, dX, dY, height, length);
        destIsOnTheRight = isInRectangle(pos, dX, dY, height, -length);
        destIsAhead = isInRectangle(pos, dX, dY, length, height);
        destIsBehind = isInRectangle(pos, dX, dY, -length, height);
    } else if (robTheta == -pi) {
        inverseValue = 0.0; turnLeft = -pi/2; turnRight = pi/2;
        toCheck = robPositionX;
        robThF = allowanceFactor*HALL2_4[0];
        if(robPositionX > HALL1_3[0] && (robPositionY > HALL1_2[1] || robPositionY < HALL2_2[1])) {
            robThB = HALL1_3[0]+(1-allowanceFactor)*HALL1_3[0];
        } else {
            robThB = HALL2_1[0]+(1-allowanceFactor)*HALL2_1[0];        
        }
        destIsOnTheLeft = isInRectangle(pos, dX, dY, height, -length);
        destIsOnTheRight = isInRectangle(pos, dX, dY, height, length);
        destIsAhead = isInRectangle(pos, dX, dY, -length, height);
        destIsBehind = isInRectangle(pos, dX, dY, length, height);
    } else if (robTheta == pi/2) {
        inverseValue = -pi/2; turnLeft = -pi; turnRight = 0;
        toCheck = robPositionY;
        if(robPositionX > HALL2_2[0]) {
            robThF = allowanceFactor*HALL1_4[1];
            robThB = HALL2_4[1]+(1-allowanceFactor);
        } else {
            robThF = allowanceFactor*HALL1_2[1];
            robThB = HALL2_2[1]+(1-allowanceFactor);
        }
        destIsOnTheLeft = isInRectangle(pos, dX, dY, -length, height);
        destIsOnTheRight = isInRectangle(pos, dX, dY, length, height);
        destIsAhead = isInRectangle(pos, dX, dY, height, length);
        destIsBehind = isInRectangle(pos, dX, dY, height, -length);
    } else if (robTheta == -pi/2) {
        inverseValue = pi/2; turnLeft = 0; turnRight = -pi;      
        toCheck = robPositionY;
        if(robPositionX > HALL2_2[0]) {
            robThF = allowanceFactor*HALL1_4[1];
            robThB = HALL2_4[1]+(1-allowanceFactor);
        } else {
            robThF = allowanceFactor*HALL1_2[1];
            robThB = HALL2_2[1]+(1-allowanceFactor);
        }
        destIsOnTheLeft = isInRectangle(pos, dX, dY, length, height);
        destIsOnTheRight = isInRectangle(pos, dX, dY, -length, height);
        destIsAhead = isInRectangle(pos, dX, dY, height, -length);
        destIsBehind = isInRectangle(pos, dX, dY, height, length);
    }

    // should change direction if wall is close, 
    // or destination is not reachable with current direction
    if(robTurned) {
        if(destIsAhead) {
            return;
        } else if(destIsBehind || toCheck < robThB || toCheck > robThF) { //should turn 180 deg
            robTheta = inverseValue;
            robTurned = false;
        } else if (destIsOnTheLeft) { //should turn left
            robTheta = turnLeft;
            robTurned = false;
        } else if (destIsOnTheRight) { //should turn right
            robTheta = turnRight;
            robTurned = false;
        } 
    } else {
       robTurned = toCheck > robThB && toCheck < robThF;     
    }
}

double calculateDistance() {
    double xdifference = pow(robPositionX-humanPositionX[currH-1], 2);
    double ydifference = pow(robPositionY-humanPositionY[currH-1], 2);
    double distance = sqrt(xdifference + ydifference)/100;
    if(distance<0) {
        return -distance;
    } else {
        return distance;
    }
}

void checkSuccess() {
    bool robXinDestInterval = robPositionX >= destX[currH-1]-25.0 && robPositionX <= destX[currH-1]+25.0;
    bool robYinDestInterval = robPositionY >= destY[currH-1]-25.0 && robPositionY <= destY[currH-1]+25.0;
    humanRobotDist = calculateDistance();

    if(robXinDestInterval && robYinDestInterval) {
        // If human currently served is not leading,
        // the robot controller can determine when
        // he/she is served
        if(humanRobotDist < 1 && patterns[currH-1]==0) {
            humanCanStart = false;
            served[currH-1] = true;
            currH++; 
        } else if (humanRobotDist < 1 && patterns[currH-1] == 1 && served[currH-1] == true) {
            currH++;            
        } else if (patterns[currH-1] == 2 && !objectTaken[currH-1]) {
            humanCanStart = true;
            objectTaken[currH-1] = true;
            destX[currH-1] = humanPositionX[currH-1];
            destY[currH-1] = humanPositionY[currH-1];
        } else if (patterns[currH-1] == 2 && objectTaken[currH-1]) {
            humanCanStart = false;
            served[currH-1] = true;
            currH++;
        }
    }
    // If all humans have been served, 
    // mission is accomplished
    if(served[humansToServe-1]) {
       success = true;
    }
}

void checkActions() {
    bool robXinDestInterval , robYinDestInterval;

    checkSuccess();
    if(!success && currH <= humansToServe) {
        // distance between human and robot [m]
        humanRobotDist = calculateDistance();
        // determines when to change direction, 
        // based on curr position and floor plan
        changeDirection();

        if(!recharge && batteryCharge<=rechargingTh 
                && ((!aStart && patterns[currH-1]==1)|| patterns[currH-1]!=1)) { // when to move to recharge station
            recharge = true;
            stopRecharge = false;
            aStart = true;
            aStop = false;
        } else if(aStart && recharge) { // when to stop because recharge station has been reached
            robXinDestInterval = robPositionX >= rechargeStation[0]-50.0 && robPositionX <= rechargeStation[0]+50.0;
            robYinDestInterval = robPositionY >= rechargeStation[1]-50.0 && robPositionY <= rechargeStation[1]+50.0;
            if(robXinDestInterval && robYinDestInterval) {
                aStart = false;
                aStop = true;
            }            
        } else if(recharge && batteryCharge>=stopRechargingTh) { // when to stop recharging
            recharge = false;
            stopRecharge = true;
        } else if(!recharge && aStart && (batteryCharge<=rechargingTh || humanFatigue[currH-1]>=stopFatigue)) { // stop action due to internal reasons
            aStart = false;
            aStop = true; 
        } else if(aStart && (!humanCanStart || humanRobotDist > 2)) { // stop due to excessive human-robot distance
            if(!humanCanStart && humanRobotDist < 2) {
                humanCanStart = patterns[currH-1] == 0;
            } else {
                aStart = false;
                aStop = true;
            }        
        } else if(!recharge && !aStart && humanFatigue[currH-1]<=resumeFatigue) { // when to start action
            aStart = true;
            aStop = false;
            if(humanRobotDist < 2) {
                humanCanStart = patterns[currH-1] == 0;
            }
        } else if(batteryCharge<=failBattery || humanFatigue[currH-1]>=failFatigue) { // when mission fails
            fail = true;
        }
    }
}

void resetActions() {
    recharge = false;
    stopRecharge = false;
    aStart = false;
    aStop = false;
}

